<!DOCTYPE html><html lang="en"><head>    <title>YOURDORM</title>    <meta charset="utf-8">    <meta name="viewport" content="width=device-width, initial-scale=1">    <meta name="format-detection" content="telephone=no">    <link href="{{ URL::to('/') }}/css/bootstrap.min.css" rel="stylesheet">    <link href="{{ URL::to('/') }}/css/style1.css" rel="stylesheet">    <link href="https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" rel="stylesheet">    <link rel="icon" href="" type="image/gif"></head><body><nav class="navbar navbar-default navbar-fixed-top">    <div class="container-fluid">        <div class="navbar-header">            <button type="button" class="navbar-toggle collapsed" data-toggle="collapse" data-target="#navbar"                    aria-expanded="false" aria-controls="navbar">                <span class="sr-only">Toggle navigation</span>                <span class="icon-bar"></span>                <span class="icon-bar"></span>                <span class="icon-bar"></span>            </button>            <a class="navbar-brand" href="#">                <img class="navlogo" src="{{ URL::to('/') }}/images/yourdormlogo.jpg" alt="">            </a>        </div>        <div id="navbar" class="navbar-collapse collapse">            <ul class="nav navbar-nav">                <li class="active"><a href="#">HOME</a></li>                <li class="dropdown">                    <a href="#" class="dropdown-toggle" data-toggle="dropdown" role="button" aria-haspopup="true"                       aria-expanded="false">PACKAGES <span class="caret"></span></a>                    <ul class="dropdown-menu">                        <li><a href="#">INTERNATIONAL</a></li>                        <li role="separator" class="divider"></li>                        <li><a href="#">DOMESTIC</a></li>                    </ul>                </li>                <li><a href="#">THE JOURNAL</a></li>                <li><a href="#">ABOUT US</a></li>                <li><a href="#"> CONTACT</a></li>            </ul>            <ul class="nav navbar-nav navbar-right">                <li><a href="#"><i class="glyphicon glyphicon-shopping-cart"></i></a></li>            </ul>        </div><!--/.nav-collapse -->    </div></nav><div class="container-fluid" style="padding: 0px;background-color: #f1f1f1">    <div class="inn-banner">    </div>    <div class="container">        <div class="col-md-12 formmaindiv">            <form action="{{ url('/request_search') }}" method="post">                {{ csrf_field() }}                <div class="col-md-6">                    <div class="form-group">                        <label for="usr">First Name*:</label>                        <input type="text" name="first_name" class="form-control" id="first_name">                    </div>                </div>                <div class="col-md-6">                    <div class="form-group">                        <label for="pwd">Last Name*:</label>                        <input type="text" name="last_name" class="form-control" id="last_name">                    </div>                </div>                <div class="col-md-6">                    <div class="form-group">                        <label for="usr">Email*:</label>                        <input type="email" name="email" class="form-control" id="email">                    </div>                </div>                <div class="col-md-6">                    <div class="form-group">                        <label for="pwd">Phone*:</label>                        <input type="number" name="phone" class="form-control" id="phone">                    </div>                </div>                <label for="pwd" style="padding-left: 13px;">Type of Property*:</label>                <div class="col-md-12" style="margin-bottom: 30px">                    <div class="col-md-2">                        <label class="checkbox-inline"><input type="checkbox" name="property_type" class="property_type" value="">Room</label>                    </div>                    <div class="col-md-2">                        <label class="checkbox-inline"><input type="checkbox" name="property_type" class="property_type" value="">Apartment</label>                    </div>                    <div class="col-md-2">                        <label class="checkbox-inline"><input type="checkbox" name="property_type" class="property_type" value="">House</label>                    </div>                </div>                <div class="col-md-6">                    <div class="form-group">                        <label for="sel1"># of Bedrooms:</label>                        <select class="form-control" name="no_of_bedroom" id="sel1">                            <option value="1">1</option>                            <option value="2">2</option>                            <option value="3">3</option>                            <option value="4">4</option>                            <option value="5">5</option>                            <option value="6">6</option>                            <option value="7">7</option>                            <option value="8">8</option>                        </select>                    </div>                </div>                <div class="col-md-6">                    <div class="form-group">                        <label for="pwd">University/College*:</label>                        <input type="number" name="uni_college" class="form-control" id="uni_college">                    </div>                </div>                <div class="col-md-12" style="margin-bottom: 30px">                    <label for="pwd" style="padding-left: 13px;">Form of Transportation*:</label>                    <div class="radio">                        <label><input type="radio" class="transportation" name="transportation">Walking</label>                    </div>                    <div class="radio">                        <label><input type="radio" class="transportation" name="transportation">Bus</label>                    </div>                    <div class="radio ">                        <label><input type="radio" class="transportation" name="transportation" disabled>Car</label>                    </div>                </div>                <div class="col-md-12">                    <label for="pwd" style="padding-left: 13px;">Distance to University/College (based on previous                        answer)*:</label>                    <div class="radio">                        <label><input type="radio" class="distance_of_uni" name="distance_of_uni">5-15 min</label>                    </div>                    <div class="radio">                        <label><input type="radio" class="distance_of_uni" name="distance_of_uni">15-30 min</label>                    </div>                    <div class="radio ">                        <label><input type="radio" class="distance_of_uni" name="distance_of_uni" disabled>30-40 min</label>                    </div>                    <div class="radio ">                        <label><input type="radio" class="distance_of_uni"name="distance_of_uni" disabled>N/A</label>                    </div>                </div>                <div class="col-md-6">                    <div class="form-group">                        <label for="usr">Price Range (Per Month)*:</label>                        <input type="text" class="form-control" name="price_range_per_month" id="price_range_per_month">                    </div>                </div>                <div class="col-md-12">                    <label for="pwd" style="padding-left: 13px;">Length of Lease*:</label>                    <div class="radio">                        <label><input type="radio" class="length_of_lease" name="length_of_lease">4 months</label>                    </div>                    <div class="radio">                        <label><input type="radio" class="length_of_lease" name="length_of_lease">8 months</label>                    </div>                    <div class="radio ">                        <label><input type="radio" class="length_of_lease" name="length_of_lease">12 months</label>                    </div>                    <div class="radio ">                        <label><input type="radio" class="length_of_lease" name="length_of_lease">12+ months</label>                    </div>                </div>                <div class="col-md-6">                    <div class="form-group">                        <label for="usr">Move-in Date*:</label>                        <input type="date" class="form-control" name="move_in_date" id="move_in_date">                    </div>                </div>                <div class="col-md-12">                    <label for="pwd" style="padding-left: 13px;">Parking*:</label>                    <div class="radio">                        <label><input type="radio" value="yes" class="parking" name="parking">Yes</label>                    </div>                    <div class="radio">                        <label><input type="radio" value="no" class="parking" name="parking">No</label>                    </div>                </div>                <div class="col-md-12">                    <label for="pwd" style="padding-left: 13px;width: 100%">Additional Comments:</label>                    <textarea name="additional_comments"  id="" style="    width: 100%;    height: 85px;    border-radius: 5px;"></textarea>                </div>                <div class="col-md-12">                    <script                            src="https://checkout.stripe.com/checkout.js" class="stripe-button"                            data-key="{{ env('STRIPE_PUB_KEY') }}"                            data-amount="1999"                            data-name="Your Drom"                            data-label="Search"                            data-description="Online course about integrating Stripe"                            data-image="https://stripe.com/img/documentation/checkout/marketplace.png"                            data-locale="auto"                            data-currency="usd">                    </script>                    {{--<button type="submit" name="form[Submit]" id="Submit" class=" btn btn-danger rsform-submit-button" onClick="alert('Form submitted');">Submit</button>--}}                </div>            </form>        </div>    </div></div><script src="{{ URL::to('/') }}/js/jquery-3.3.1.min.js"></script><script src="{{ URL::to('/') }}/js/bootstrap.min.js"></script>{{--<script>--}}{{--jQuery("#Submit").prop('disabled', true);--}}{{--var toValidate = jQuery('#first_name, #last_name, #email, #phone, .property_type, #uni_college, .transportation,.distance_of_uni,#price_range_per_month,.length_of_lease,#move_in_date,.parking'),--}}{{--valid = false;--}}{{--toValidate.keyup(function () {--}}{{--if (jQuery(this).val().length > 0) {--}}{{--jQuery(this).data('valid', true);--}}{{--} else {--}}{{--jQuery(this).data('valid', false);--}}{{--}--}}{{--toValidate.each(function () {--}}{{--if (jQuery(this).data('valid') == true) {--}}{{--valid = true;--}}{{--} else {--}}{{--valid = false;--}}{{--}--}}{{--});--}}{{--if (valid === true) {--}}{{--jQuery("#Submit").prop('disabled', false);--}}{{--} else {--}}{{--jQuery("#Submit").prop('disabled', true);--}}{{--}--}}{{--});--}}{{--</script>--}}</body></html>